set<int> g_Results;
vector<int> g_Mama;

int findPrimePowerNumber(int N);
int calSquare(int x);
int calCube(int x);
int calFourthPower(int x);

int calSquare(int x) {
	return (int)pow(x, 2);
}

int calCube(int x) {
	return (int)pow(x, 3);
}

int calFourthPower(int x) {
	return (int)pow(x, 4);
}

void generate() {
	for (int k = 0; k < g_Size2; k++) {
		for (int i = 0; i < g_Size34; i++) {
			int sum = g_Primes2[k] + g_Sum34[i];
			if (sum < 10000000) {
				g_Results.insert(sum);
			}
		}
	}

	g_Mama = vector<int>(g_Results.begin(), g_Results.end());
}

int findPrimePowerNumber(int N) {
	int pos = upper_bound(g_Mama.begin(), g_Mama.end(), N) - g_Mama.begin();

	return pos;
}

int main() {

	int T, N;

	generate();

	cin >> T;
	for (int i = 0; i < T; i++) {
		cin >> N;
		cout << findPrimePowerNumber(N) << "\n";
	}

	return 0;
}
